import React, { Component } from 'react';
import Story from './Story';



class ShowList extends React.Component {
	constructor() {
    super();
    this.state = {
      story: "",
      username: ""
    };
  }
	  render() {
			let data;
      let user_name;
      let getStory
      let getUsername
		return (
		  <div>
					{this.props.items.map((object, i) => {
						if (object.message) {
							data = <div className={"row"} key={i}> 
											<b key={i}> {object.message}</b> 
										</div>;
						}
						if (object.story) {
              getStory = () => {
                return this.setState({
                  story: object.story
                })
              }
              console.log(this.state.story)
              // return <Story value={this.state.story} name={this.state.username} />

              data = <Story value={this.state.getStory}/>; 

						}
						if (object.from) {
              user_name = <b key={i}> {object.from.name}</b> 
              getUsername = () => {
                return this.setState({
                  username: object.from.name
                })
              }
						}
						if(object.full_picture){
							data = <div className={"row"} key={i}>
											<img src={object.full_picture} id="picture_post"/>
										</div>; 
						}
						if (object.source) {
							data = <div className={"video_sec"} key={i}><video
							src={object.source}
							controls
							autplay="true"
							playsinline
							muted
							crossorigin
							width = "570"
						/></div>;
						}

						return <div className="coupon">
						<div className="container">
							<div className="chip">
								<img src="img_avatar.png" alt="Person" width="96" height="96"/>
								{user_name}
							</div>
						</div>
						<div className="container" >
							{data}
						<p></p>
						</div>
						<div className="container">
						</div>
					</div>;

					})}
				</div>
		);
	  }
	}


export default ShowList;
